mapper.select:
SELECT DISTINCT a.id AS "testId", a.title AS "questionTitle", a.class AS "questionClass", a.type, a.select_num, a.file_path, b.course_name AS "courseName", a.course_id, a.status, a.create_by, a.create_time FROM (hotel_ceshi_question a, hotel_course b, hotel_ceshi_knowledge c) WHERE c.question_id = a.id AND a.course_id = b.course_id
AND a.create_by = #{createUser}
AND b.course_name LIKE CONCAT('%', #{courseName}, '%')
AND c.knowledge_id IN(${knowledgeId})
AND b.course_id =${courseId}
AND a.title LIKE CONCAT('%', #{questionTitle}, '%')
AND a.type =${type}
ORDER BY a.create_time DESC

SELECT DISTINCT a.id AS "testId", a.title AS "questionTitle", a.class AS "questionClass", a.type, a.select_num, a.file_path AS "questionFilePath", a.course_id, a.status, a.create_by, b.answer_content, a.create_time FROM (hotel_ceshi_question a, hotel_ceshi_answers b, hotel_ceshi_knowledge c, hotel_course d) WHERE c.question_id = a.id AND a.id=b.question_id AND a.course_id = d.course_id AND a.status='1'
AND a.class = #{questionClass}
AND c.knowledge_id IN(${knowledgeId})
AND a.course_id = #{courseId}

mapper.insert:
INSERT INTO hotel_ceshi_knowledge(
		question_id, 
		knowledge_id
)
VALUES(
		#{testId}, 
		#{knowledgeId}
)

mapper.update:
UPDATE hotel_ceshi_question 
SET 	course_id = #{courseId},
		title = #{questionTitle},
		class = #{questionClass},
		type = #{type},
		select_num = #{selectNum},
		status = #{status},
	
		file_path = #{questionFilePath},
	
		update_time = #{updateTime}
WHERE id = #{testId}

UPDATE hotel_ceshi_paper_questions 
SET question_score = #{questionScore}
WHERE paper_id = #{paperId}
AND question_id =#{questionId}

UPDATE hotel_ceshi_answers 
SET 	answer_content = #{answerContent}, 
		add_info = #{answerRemark}, 
		update_time = #{updateTime}
WHERE question_id = #{testId}
